import { useState } from "react";

import {
  MenuAlt1Icon,
  UserIcon,
  StarIcon,
  CodeIcon,
  PaperClipIcon,
  MailIcon,
} from "@heroicons/react/outline";

const Nav = () => {
  const [open, setOpen] = useState(true);

  const Menus = [
    { title: "About" },
    { title: "Skills" },
    { title: "Projects" },
    { title: "Resume", gap: true },
    { title: "E-mail" },
    { title: "LinkedIn", gap: true },
    { title: "GitHub" },
    { title: "Discord" },
  ];

  return (
    <div className="flex">
      <div
        className={`${
          open ? "w-72" : "w-20"
        } duration-300 h-screen bg-[#3E3E3E] text-white relative shadow-inner-5lg`}
      >
        <MenuAlt1Icon
          className={`absolute cursor-pointer rounded-full -right-3 top-9 w-7 border-2 border-[#3E3E3E] fill-[#3E3E3E] ${
            !open && "rotate-180"
          }`}
          onClick={() => setOpen(!open)}
        />

        <div className="flex gap-x-4 ml-14 pt-16 ">
          <h1
            className={` text-white origin-center font-medium text-xl duration-200 ${
              !open && "scale-0"
            }`}
          >
            Kaviya Gopi
          </h1>
        </div>
        <ul className="pt-14 ml-16">
          {Menus.map((Menu, index) => (
            <li
              key={index}
              className={`flex  rounded-md p-2 cursor-pointer hover:bg-light-white text-gray-300 text-lg items-center gap-x-8 
              ${Menu.gap ? "mt-16" : "mt-8"} ${
                index === 0 && "bg-light-white"
              } `}
            >
              <span className={`${!open && "hidden"} origin-left duration-200`}>
                {Menu.title}
              </span>
            </li>
          ))}
        </ul>
      </div>
      <div className=" p-7 text-2l font-semibold flex-1 h-screen">
        <h1>Home Page</h1>
      </div>
    </div>
  );
};

export default Nav;
