import { useState } from "react";
import "bootstrap-icons";
import {
  MenuAlt1Icon,
  UserIcon,
  StarIcon,
  CodeIcon,
  PaperClipIcon,
  MailIcon,
} from "@heroicons/react/outline";

const Nav = () => {
  const [open, setOpen] = useState(true);

  const Menus = [
    { title: "About" },
    { title: "Skills" },
    { title: "Projects" },
    { title: "Resume", gap: true },
    { title: "E-mail" },
    { title: "LinkedIn" },
    { title: "GitHub" },
    { title: "Discord" },
  ];

  return (
    <div className="flex">
      <div
        className={`${
          open ? "w-72" : "w-20"
        } duration-300 h-screen bg-[#3E3E3E] text-white relative shadow-inner-5lg`}
      >
        <MenuAlt1Icon
          className={`absolute cursor-pointer rounded-full -right-3 top-9 w-7 border-2 border-[#3E3E3E] fill-[#3E3E3E] ${
            !open && "rotate-180"
          }`}
          onClick={() => setOpen(!open)}
        />

        <div className="flex gap-x-4 items-center">
          <h1
            className={`text-white origin-left font-medium text-xl duration-200 ${
              !open && "scale-0"
            }`}
          >
            Kaviya Gopi
          </h1>
        </div>
        <div>
          <button
            className={
              window.location.pathname === "/dashboard" ? style.btn : style.btno
            }
          >
            <ViewGridIcon className="w-8" />
            &nbsp;Dashboard
          </button>

          <button
            className={
              window.location.pathname === "/transactions"
                ? style.btn
                : style.btno
            }
          >
            <SwitchVerticalIcon className="w-8" />
            &nbsp;Transactions
          </button>

          <button
            className={
              window.location.pathname === "/additems" ? style.btn : style.btno
            }
          >
            <PlusIcon className="w-8" />
            &nbsp;Add Items
          </button>

          <button
            className={
              window.location.pathname === "/adduser" ? style.btn : style.btno
            }
          >
            <UserAddIcon className="w-8" />
            &nbsp;Add User
          </button>
        </div>
      </div>
      <div className=" p-7 text-2l font-semibold flex-1 h-screen">
        <h1>Home Page</h1>
      </div>
    </div>
  );
};

export default Nav;
