import { useState } from "react";

import { MenuAlt1Icon, ChevronDoubleRightIcon } from "@heroicons/react/outline";

const Nav = () => {
  const [open, setOpen] = useState(false);

  const Menus = [
    { title: "About", link: "/", active: window.location.pathname === "/" },
    {
      title: "Skills",
      link: "/skills",
      active: window.location.pathname === "/skills",
    },
    {
      title: "Projects",
      link: "/projects",
      active: window.location.pathname === "/projects",
    },
    {
      title: "Resume",
      gap: true,
      link: "https://drive.google.com/file/d/1tD6HvNdRcr5S2htUaveSNTXFSaDTP50K/view?usp=sharing",
      active: window.location.pathname === " ",
      target: "_blank",
    },
    { title: "E-mail", link: "/", active: window.location.pathname === "/" },
    {
      title: "LinkedIn",
      gap: true,
      link: "/",
      active: window.location.pathname === "/",
    },
    { title: "GitHub", link: "/", active: window.location.pathname === "/" },
    { title: "Discord", link: "/", active: window.location.pathname === "/" },
  ];

  return (
    <div className="flex fixed top-0">
      <div
        className={`${
          open ? "w-72" : "w-20"
        }  h-screen bg-[#3E3E3E] text-white relative pl-6 duration-300`}
      >
        <ChevronDoubleRightIcon
          className={`absolute cursor-pointer rounded-full -right-3 top-16 w-7 fill-[#3E3E3E] ${
            !open && "rotate-180"
          }`}
          onClick={() => setOpen(!open)}
        />

        <div className="flex gap-x-4 ml-14 pt-16 ">
          <h1
            className={` text-white origin-left font-medium text-xl duration-200 ${
              !open && "scale-0"
            }`}
          >
            Kaviya Gopi
          </h1>
        </div>
        <ul className="pt-14 ml-16">
          {Menus.map((Menu, index) => (
            <li
              key={index}
              className={`flex  rounded-md p-2 cursor-pointer text-gray-300 text-lg items-center gap-x-8 
              ${Menu.gap ? "mt-16" : "mt-8"} ${
                index === 0 && "bg-light-white"
              } `}
            >
              <span
                className={`${!open && "hidden"} ${
                  Menu.active && "text-red-600"
                } origin-left duration-200`}
              >
                {Menu.title}
              </span>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Nav;
