import { useState } from "react";
import { MenuAlt1Icon } from "@heroicons/react/outline";

const Nav = () => {
  const [open, setOpen] = useState(true);
  return (
    <div className="flex">
      <div
        className={`${
          open ? "w-72" : "w-20"
        } duration-300 h-screen bg-[#3E3E3E] text-white relative shadow-lg`}
      >
        <MenuAlt1Icon
          className={`absolute cursor-pointer rounded-full -right-3 top-9 w-7 border-2 border-[#3E3E3E] fill-[#3E3E3E] ${
            !open && "rotate-180"
          }`}
          onClick={() => setOpen(!open)}
        />
      </div>
      <div className=" p-7 text-2l font-semibold flex-1 h-screen"></div>
      <h1>Home Page</h1>
    </div>
  );
};

export default Nav;
